create view event_view (PIN, ACCOUNT_TRANSACTION_DT, ACCOUNT_TRANSACTION_CD, ACCOUNT_TRANSACTION_SYS_ID, PARTITION_SYS_ID, POSTED_CYC_ID, UNPOSTABLE_CD, VALIDITY_CD, INPUT_TRANSACTION_TXT)
as
select ACCOUNT_TRANSACTION.PIN, ACCOUNT_TRANSACTION.ACCOUNT_TRANSACTION_DT, ACCOUNT_TRANSACTION.ACCOUNT_TRANSACTION_CD,
ACCOUNT_TRANSACTION.ACCOUNT_TRANSACTION_SYS_ID, ACCOUNT_TRANSACTION.PARTITION_SYS_ID, ACCOUNT_TRANSACTION.POSTED_CYC_ID,
ACCOUNT_TRANSACTION.UNPOSTABLE_CD, ACCOUNT_TRANSACTION.VALIDITY_CD, INPUT_TRANSACTION.INPUT_TRANSACTION_TXT
from ACCOUNT_TRANSACTION, INPUT_TRANSACTION where ACCOUNT_TRANSACTION.ACCOUNT_TRANSACTION_SYS_ID=INPUT_TRANSACTION.ACCOUNT_TRANSACTION_SYS_ID
order by ACCOUNT_TRANSACTION_DT